import React,{ useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import * as yup from 'yup';
import Styled from "styled-components";
import dummyData from "../dummy-data";

const StyledForm = Styled.div`
display: flex;
  align-items: center;
  flex-direction: column;
  background-color: crimson;
  font-size: 20px;
  padding: 3%;
`

export default function SearchBox(){
 const [ formState, setformState ] = useState ({ searchtext: "" });
 const [ errors, setErrors ] = useState({ searchbar: "" });
 const [ buttonDisabled, setButtonDisabled ] = useState(true);

 const formSchema = yup.object().shape({ 
     searchtext: yup.string().required("Cannot search empty")
 });

 useEffect(()=>{
     formSchema.isValid(formState).then((valid)=>{
         console.log("Is the form Valid?", valid);
         setButtonDisabled(!valid);
     });
 },[formState]);

const validate =(event) =>{
    yup
    .reach (formSchema, event.target.name)
    .validate(event.target.value)
    .then((validate)=>{
        setErrors({})
    })
}

const inputChange = (event) => {
    event.persist();
    const newFormState = {...formState,[event.target.name]:event.target.value};
    setformState(newFormState);
};


    return(
        <StyledForm> 
            <div>
                <form>
                <label htmlFor = "searchtext">
                Insert Text
                <br/>
                <input 
                id="searchtext"
                type="text"
                name="searchtext"
                value={formState.searchtext}
                onChange={inputChange}
                />
                </label>
                <br/>
                <button type="submit" disabled={buttonDisabled}>Search</button>            
                </form>
            </div>

            <div>
                <div>CREATED TITLE</div>
                <button>Edit</button>
                <button>Delete</button>
            </div>
            <button>Add How to's</button>
        </StyledForm> 
    );
}
