import React, { useState } from 'react';
import { Button } from 'reactstrap';
import './EditTopic.css'

const EditTopic = (props) => {
	const [topicContent, setTopicContent] = useState(props.item);
	console.log(topicContent)

	return (
		<form>
			<label htmlFor="title">
				Title:
				<input type="text" name="title" id="title" value={topicContent.title}/>
			</label>
			<label htmlFor="content">
				Content:
				<textarea type="text" name="content" id="content" value={topicContent.content}/>
			</label>

			<Link>
				<Button color="success">Save</Button>
			</Link>

		</form>
	);
};

export default EditTopic;
