import React,{ useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import * as yup from 'yup';
import Styled from "styled-components";
import dummyData from "../dummy-data";

const StyledForm = Styled.div`
display: flex;
  align-items: center;
  flex-direction: column;
  background-color: crimson;
  font-size: 20px;
  padding: 3%;
`

export default function SearchBox(){
 const [ formState, setformState ] = useState ({ searchbar: "" });
//  const [ errors, setErrors ] = useState({ searchbar: "" });
//  const [ buttonDisabled, setButtonDisabled ] = useState(true);
//  const history = seHistory();
 const formSchema = yup.object.shape({ 
     searchtext: yup.string().required("Cannot search empty")
 });

//  useEffect(()=>{
//      formSchema.isValid(formState).then((valid)=>{
//          console.log("Is the form Valid?", valid);
//          setButtonDisabled(!valid);
//      });
//  },[formState]);

const inputChange = (event) => {
    event.persist();
    };


    return(
        <StyledForm> 
            <div>
                <form>
                <label htmlFor = "searchtext">
                Insert Text
                <br/>
                <textarea 
                id="searchtext"
                name="searchtext"
                value={formState.searchtext}
                onChange={inputChange}
                />
                </label>
                <br/>
                <button>Search</button>            
                </form>
            </div>

            <div>
                <div>CREATED TITLE</div>
                <button>Edit</button>
                <button>Delete</button>
            </div>
            <button>Add How to's</button>
        </StyledForm> 
    );
}
